import React, { useState } from 'react';
import { LocationFrame } from './locationFrame';
import { database } from './mockBackend/database';

export function Location(props) {

    const [location, setLocation] = useState({
        id: '0',
        name: 'Placeholder',
        date: '01.02',
        weather: 'sun',
        temp: '18',
        wind: '0'
    })

    const datasource = database;

    const searchDatabase = (datasource) => {

        const locationData = datasource.filter((dataLocation) => {
            return dataLocation.name === location.name;
        })

        if (locationData !== null) {
            setLocation([{
                id: props.generateId(),
                name: locationData.name,
                date: locationData.date,
                weather: locationData.weather,
                temp: locationData.temp,
                wind: locationData.wind
            }])
        } else {
            alert('There exists no location with that name.');
        }
    }

    return (
        <div className=''>
            <LocationFrame key={location} id={location.id} name={location.name} weather={location.weather} temp={location.temp} wind={location.wind}/>
        </div>
    )
}
