//This arrow and its corresponding JavaScript, HTML and CSS has been borrowed from user Matt Braun at CodePen.
//Link: https://codepen.io/mattbraun/pen/EywBJR
//Credit: Matt Braun

import React, { useState } from 'react';
import '/Users/sondremelhus/DEV/ReactApps/weatherapp/src/App.css';

export function Arrow(){

    const [toggle, setToggle] = useState('arrow-icon');

    const handleClick = (event) => {
        event.preventDefault();
        if (toggle === 'arrow-icon') {
            setToggle('open');
        } else {
            setToggle('arrow-icon');
        }
    } 

    return (
        <div onClick={handleClick}>
            <p className={toggle}>
                <span className="left-bar"></span>
                <span className="right-bar"></span>
            </p>
        </div>
    )
}
