//Koden er basert på: https://blog.logrocket.com/build-react-autocomplete-component/

import React, {useState} from 'react';

const AutoComplete = ({ locations }) => {

    const [filteredLocations, setFilteredLocations] = useState([]);
    const [activeLocationIndex, setActiveLocationIndex] = useState(0);
    const [showLocations, setShowLocations] = useState(false);
    const [input, setInput] = useState('');

    //Used to handle a change in the input field
    const handleChange = (event) =>  {
        const userInput = event.target.value;

        //Filter out the locations that doesnt contain the users input
        const unLinked = locations.filter(
            (location) => 
                location.toLoweCase().indexOf(userInput.toLoweCase()) > -1
        );

        setInput(event.target.value);
        setFilteredLocations(unLinked);
        setActiveLocationIndex(0);
        setShowLocations(true);
    };

    const handleClick = (event) => {
        setFilteredLocations([]);
        setInput(event.target.innerText);
        setActiveLocationIndex(0);
        setShowLocations(false);
    }

    const handleKeyDown = (event) => {

        if (event.keyCode === 13) {
            handleClick();
        }
        if (event.keyCode === 38) {

            if (activeLocationIndex === 0) {
                setInput('');
            } else {
                setActiveLocationIndex((prev) => {
                    return prev - 1;
                })
            }
        }
        if (event.keyCode === 40) {

            if (activeLocationIndex === filteredLocations.length) {
                return null;
            } else {
                setActiveLocationIndex((prev) => {
                    return prev + 1;
                });
            }
        }
    }

    const locationListComponent = () => {
        return filteredLocations.length ? (
            <ul class='locations'>
                {filteredLocations.map((location, index) => {
                    let className;

                    if (index === activeLocationIndex) {
                        className = 'location-active';
                    }
                    return (
                        <li className={className} key={location} onClick={handleClick}>
                            {location}
                        </li>
                    );
                })}
            </ul>
        ) : (
            <div class='no-locations'>
                <em>No locations by that name</em>
            </div>
        )
    }

    return (
        <div>
            <input 
                type='text'
                onChange={handleChange}
                onKeyDown={handleKeyDown}
                value={input}
            />
        </div>
    )
}
