//This code has been borrowed from: https://github.com/kaxada/search-bar/blob/main/src/App.js
//All credit goes to the original author

import { useState } from "react";
import Trie from "./trie.js";
import { database } from '../mockBackend/database';
import './SearchbarStyle.css';

export function SearchBar(props) {
  const [prefix, setPrefix] = useState("");
  const [suggestion, setSuggestion] = useState("");

  // const getWords = async() => {
  //   const url = 'https://raw.githubusercontent.com/EKaxada/webster-words/main/dict.json'
  //   const res = await fetch(url, {
  //     method: 'GET'
  //   });
  //   return await res.json();
  // }

  const data = database;
    const dictionary = data.map(locations => locations.name);

  var myTrie = new Trie();

  (async()=>{
    // const dictionary = await getWords();
    for (let i = 0; i < dictionary.length; i++) {
        const location = dictionary[i];
        myTrie.insert(location)
    }
  })();

  const handleChange = (event) => {

    let inputValue = event.target.value;
    props.setText(inputValue);
    setPrefix(inputValue);

    let locations = inputValue.split(" ");
    let triePrefix = locations[locations.length - 1].toLowerCase();
    let foundLocations = myTrie.find(triePrefix).sort((a, b) => {
      return a.length - b.length;
    });

    let firstLocation = foundLocations[0];

    if (
        foundLocations.length !== 0 &&
        inputValue !== "" &&
        inputValue[inputValue.length - 1] !== " "
    ) {
      if (firstLocation != null) {
        let remainder = firstLocation.slice(triePrefix.length);
        setSuggestion(inputValue + remainder);
      }
    } else {
      setSuggestion(inputValue);
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === 'ArrowDown') {
      setPrefix(suggestion);
      props.setText(suggestion);
      props.handleSubmit();
    } 

    if (event.key === 'Enter') {
      props.setText(suggestion);
      props.handleSubmit();
      setPrefix('')
      setSuggestion('')
    }
  };

  return (
    <div className="App-Searchbar">
      <input
        type="text"
        name="search-bar"
        id="search-bar"
        placeholder="Search..."
        value={prefix}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
      />
      <input
        type="text"
        name="search-bar"
        id="search-bar2"
        value={suggestion}
      />
    </div>
  );
}
