import React, { useState } from 'react';
import { LocationFrame} from './locationFrame';
import weatherLogo from '../resources/WeatherIcons/logo.png';
import '../App.css';

export function Forecaster() {

    const [locations, setLocations] = useState([{
        locationId: ''
    }]);

    const addLocation = (newLocation) => {
        setLocations((prev) => {
            return [...prev, newLocation]
        })
    };

    const removeLocation = (locationIdToRemove) => {
        setLocations((locations) => {
            locations.filter((location) => location.id !== locationIdToRemove)
        });
    }

    return (
        <div>
            <img src={weatherLogo} alt="logo" className='App-logo' />
            <br/>
            <h1 className='App-title'>Forecaster</h1>
            {locations.map((location) => {
                <LocationFrame />
            })}
        </div>
    )
}
