//This code has been borrowed from: https://github.com/kaxada/search-bar/blob/main/src/App.js
//All credit goes to the original author

import { useState } from "react";
import Trie from "./trie.js";
import { database } from '../mockBackend/database';
import './SearchbarStyle.css';

//TODO: Fiks denne så den tar inn navnene fra database istedet for dictionary
const data = database.map(location => location.name.toLowerCase());
const dictionary = data;

export function Searchbar() {
  const [prefix, setPrefix] = useState("");
  const [suggestion, setSuggestion] = useState("");

  // const getWords = async() => {
  //   const url = 'https://raw.githubusercontent.com/EKaxada/webster-words/main/dict.json'
  //   const res = await fetch(url, {
  //     method: 'GET'
  //   });
  //   return await res.json();
  // }

  var myTrie = new Trie();

  (async()=>{
    // const dictionary = await getWords();
    const words = dictionary.words;
    for (let i = 0; i < words.length; i++) {
        const word = words[i];
        myTrie.insert(word)
    }
  })();

  const onChange = (e) => {
    var value = e.target.value;
    setPrefix(value);
    var words = value.split(" ");
    var trie_prefix = words[words.length - 1].toLowerCase();
    var found_words = myTrie.find(trie_prefix).sort((a, b) => {
      return a.length - b.length;
    });
    var first_word = found_words[0];
    if (
      found_words.length !== 0 &&
      value !== "" &&
      value[value.length - 1] !== " "
    ) {
      if (first_word != null) {
        var remainder = first_word.slice(trie_prefix.length);
        setSuggestion(value + remainder);
      }
    } else {
      setSuggestion(value);
    }
  };

  const handleKeyDown = (e) => {
    if (e.keyCode === 39) {
      setPrefix(suggestion);
    }
  };

  return (
    <div className="App">
      <input
        type="text"
        name="search-bar"
        id="search-bar"
        placeholder="Search..."
        value={prefix}
        onChange={onChange}
        onKeyDown={handleKeyDown}
      />
      <input
        type="text"
        name="search-bar"
        id="search-bar2"
        value={suggestion}
      />
    </div>
  );
}
