//Koden er basert på: https://blog.logrocket.com/build-react-autocomplete-component/

import React, { useState } from 'react';
import { database } from './mockBackend/database';
import Trie from './Searchbar/trie'
import '../App.css';


export function AddLocationForm (props) {

    const [text, setText] = useState('');
    const [suggestion, setSuggerstion] = useState('');
    const [errorMessage, setErrorMessage] = useState('');


    const data = database;
    let myTrie = new Trie();

    const getLocations = (data) => {
        const listOfLocations = [];
        for(let i = 0; i < data.length; i++) {
            listOfLocations.push(data[i].name);
        }
        return listOfLocations;
    };

    const dictionary = getLocations(data);


    (async() => {
        const locations = dictionary;
        for (let i = 0; i < locations.length; i++) {
            const location = dictionary[i];
            myTrie.insert(location);
        }
    })();

    //Metod used to change the text state to the live input from the user
    const handleInputChange = (event) => {
        let inputValue = event.target.value;

        setText(inputValue);
        setErrorMessage('');

        let value = inputValue.split(' ');
        let triePrefix = value[value.length - 1].toLowerCase();
        let foundLocations = myTrie.find(triePrefix).sort((a, b) => {
            return a.length - b.length;
        })

        let firstLocation = foundLocations[0];

        if (foundLocations.length !== 0 &&
            inputValue !== '' &&
            inputValue[inputValue.length - 1] !== '') {

                if (firstLocation !== null) {
                    let remainder = firstLocation.slice(triePrefix.length);
                    setSuggerstion(inputValue + remainder);
                }
            } else {
                setSuggerstion(inputValue);
            }
    };

    const handleKeyDown = (event) => {
        if (event.keyCode === 39) {
            setText(suggestion);
        }
    };


    //TODO: Implementer en sjekk som unngår at vi kan legge til en lokasjon to ganger.
    //Bruk props.checkIfPresent funksjon fra locationManager
    const handleSubmit = (event) => {
        event.preventDefault();

        if(text.length > 0) {
            const locationToAdd = data.find(locations => locations.name === text)

                const location = {
                    id: props.generateId(),
                    name: text,
                    date: locationToAdd.date,
                    weather: locationToAdd.weather,
                    temp: locationToAdd.temp,
                    wind: locationToAdd.wind
                }

                props.addLocation(location);
                setText('');
        } else {
            setText('');
            setErrorMessage('Please enter a valid location name.');
        }
    };

    return (
        <form onSubmit={handleSubmit} class='inputClass'>
            <input 
                type='text'
                name='searchBar'
                id='searchBar'
                placeholder='Search...'
                value={text}
                onChange={handleInputChange}
                onKeyDown={handleKeyDown}
            />
            <input
                type="text"
                name="searchBar"
                id="searchBar2"
                value={suggestion}
            />
            <p type='text' className='App-error'>{errorMessage}</p>
        </form>
    )
}
