import React from 'react';
import { useState } from 'react/cjs/react.development';
import '../App.css';

export function LocationFrame(props) {

    const [toggle, setToggle] = useState('App-weatherComp-small');
    const [toggleClose, setToggleClose] = useState('App-weatherComp-small-close');
    const { location, removeLocation } = props;

    const handleClick = (event) => {
        event.preventDefault();
        if (toggle === 'App-weatherComp-small') {
            setToggle('App-weatherComp-expanded');
            setToggleClose('App-weatherComp-expanded-close');
        } else {
            setToggle('App-weatherComp-small');
            setToggleClose('App-weatherComp-small-close');
        }
    }

    const handleRemoveClick = (event) => {
        removeLocation(location.id);
    }


    return (
        <div>
            <button onClick={handleRemoveClick} className='App-weatherComp-small-close'>x</button>
            <div onClick={handleClick} id={location.id} className={toggle}>
                <h3>{location.name}</h3>
                <img src={require(`./resources/WeatherIcons/${location.weather}.svg`)} alt='Weather icon' className='App-icons'/>
                <table style={{margin: 'auto', paddingBottom: 25}}>
                    <td style={{paddingRight: 10}}>{location.temp} &deg;</td>
                    <td style={{paddingLeft: 10}}>{location.wind} m/s</td>
                </table>
            </div>
        </div>
    )
}
