import React, { useState } from 'react';
import { LocationFrame } from './locationFrame';
import { database } from './mockBackend/database';

export function Location(props) {

    const [location, setLocation] = useState([{
        id: '0',
        name: 'Placeholder',
        date: '01.02',
        weather: 'sun',
        temp: '18',
        wind: '0'
    }])

    const locationName = props.text;
    const datasource = database;

    //Ikke en elegant løsning men enkel
    let idCounter = 0;
    const generateId = () => {
        return idCounter = idCounter + 1;
    }

    const createLocation = (dataSource) => {
        setLocation([{
            id: generateId(),
            name: dataSource.name,
            date: dataSource.date,
            weather: dataSource.weather,
            temp: dataSource.temp,
            wind: dataSource.wind
        }])
    }

    return (
        <div>
            <LocationFrame key={location} id={location.id} name={location.name} weather={location.weather} temp={location.temp} wind={location.wind}/>
        </div>
    )
}
