//Koden er basert på: https://blog.logrocket.com/build-react-autocomplete-component/

import React, { useState, useEffect } from 'react';

export function AutoComplete (props) {

    const [filteredLocations, setFilteredLocations] = useState([props.locations]);
    const [activelocation, setActivelocation] = useState('');
    const [activeLocationIndex, setActiveLocationIndex] = useState(0);
    const [showLocations, setShowLocations] = useState(false);
    const [input, setInput] = useState('');


    const handleChange = (event) => {
        const userInput = event.target.value;

        const unLinked = props.locations.filter(
            (location) => location.toLowerCase().indexOf(userInput.toLowerCase()) > -1
        );

        setInput(event.target.value);
        setFilteredLocations(unLinked);
        setActiveLocationIndex(0);
        setShowLocations(true);
    }


    const onClick = (event) => {
        event.preventDefault();

        setFilteredLocations([]);
        setInput(event.target.innerText);
        setActiveLocationIndex(0);
        setShowLocations(false);
        props.addLocation(input);
    }


    const onKeyDown = (event) => {
        event.preventDefault();

        if (event.keyCode === 13) {
            setInput(event.target.value);
        }
        if (event.keyCode === 38) {
            if (activeLocationIndex === 0) {
                setActivelocation('');
            } else {
                setActiveLocationIndex((prev) => {
                    return prev - 1;
                });
            }
        } 
        if (event.keyCode == 40) {
            if (activeLocationIndex === filteredLocations.length) {
                return null;
            } else {
                setActiveLocationIndex((prev) => {
                    return prev + 1;
                });
            }
        }
    }

    const LocationsListComponent = () => {
        return filteredLocations.length ? (
          <ul class='locations'>
            {filteredLocations.map((location, index) => {
              let className;

              if (index === activeLocationIndex) {
                className = "location-active";
              }
              return (
                <li className={className} key={location} onClick={onClick}>
                  {location}
                </li>
              );
            })}
          </ul>
        ) : (
          <div class="no-locations">
            <em>Ingen alternativer</em>
          </div>
        );
      };

    return (
        <div>
            <input 
                type='text'
                onChange={handleChange}
                onKeyDown={onKeyDown}
                value={input}
                placeholder='Enter a location...'
                />
                {showLocations && input && <LocationsListComponent />}
        </div>
    );
}
