import React, {useState, useEffect} from 'react';
import weatherLogo from './resources/WeatherIcons/logo.png';
import { Arrow } from './arrow/arrow';
import '../App.css';
import { SearchForLocation } from './searchForLocation';

export function LocationManager() {

    const [locations, setLocations] = useState([{
        location: [{
            id: '0',
            name: 'Placeholder',
            date: '01.01',
            weather: 'sun',
            temp: '18',
            wind: '0'
        }]
    }])

    let idCounter = 0;
    const generateId = () => {
        return idCounter = idCounter + 1;
    }

    const createLocation = (location) => {
        const newLocation = {
            id: generateId(),
            name: location.name,
            date: '01.01',
            weather: 'sun',
            temp: '10',
            wind: '0'
        };
    }

    const addLocation = (locationToAdd) => {
        setLocations((location) => {
            return [...location, locationToAdd];
        })
    }


    return (
        <div className='App'>
            <div className='App-header'>
                <img src={weatherLogo} alt="logo" className='App-logo' />
                <br/>
                <h1 className='App-title'>Forecaster</h1>
                <br/>
            </div>
            <Arrow />
            <SearchForLocation createLocation={createLocation} addLocation={addLocation} generateId={generateId}/>
        </div>
    )
};
