//Koden er basert p책: https://blog.logrocket.com/build-react-autocomplete-component/

import React, { useState } from 'react';
import { database } from './mockBackend/database';
import '../App.css';

export function AddLocationForm (props) {

    const [text, setText] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const datasource = database;

    //Metod used to change the text state to the live input from the user
    const handleInputChange = (event) => {
        setText(event.target.value);
        setErrorMessage('');
    }


    //TODO: Implementer en sjekk som unng책r at vi kan legge til en lokasjon to ganger.
    //Bruk props.checkIfPresent funksjon fra locationManager
    const handleSubmit = (event) => {
        event.preventDefault();

        if(text.length > 0) {
            if (props.checkIfPresent(text)) {
                const locationToAdd = datasource.find(locations => locations.name === text)

                const location = {
                    id: props.generateId(),
                    name: text,
                    date: locationToAdd.date,
                    weather: locationToAdd.weather,
                    temp: locationToAdd.temp,
                    wind: locationToAdd.wind
                }

                props.addLocation(location);
                setText('');
            } else  {
                setText('');
                setErrorMessage('This location has already been added to the list.')
            }
        } else {
            setText('');
            setErrorMessage('Please enter a valid location name.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <p type='text' value={errorMessage}></p>
            <input  type='text' 
                    value={text} 
                    onChange={handleInputChange} 
                    placeholder='Enter a location...' 
                    className='App-input'/>
        </form>
    )
}
