import React, {useState, useEffect} from 'react';
import weatherLogo from './resources/WeatherIcons/logo.png';
import { Arrow } from './arrow/arrow';
import '../App.css';
import { AddLocationForm } from './addLocationForm';

export function LocationManager() {

    const [locations, setLocations] = useState([
        {
            id: generateId(),
            name: '',
            date: '',
            weather: '',
            temp: '',
            wind: ''
        }
    ]);

    //Not an elegant solution, but it works
    let idCounter = 0;
    const generateId = () => {
        return idCounter = idCounter + 1;
    }

    //Metod used to change the state of locations and add a new location to the array
    const addLocation = (newLocation) => {
        setLocations((locations) => [newLocation, ...locations]);
    };

    //Method used to change the state of locations and remove a given location from the array based on id
    const removeLocation = (locationIdToRemove) => {
        setLocations((locations) => {
            locations.filter((location => location.id !== locationIdToRemove))
        });
    };

    return (
        <div className='App'>
            <div className='App-header'>
                <img src={weatherLogo} alt="logo" className='App-logo' />
                <br/>
                <h1 className='App-title'>Forecaster</h1>
                <br/>
            </div>
            <AddLocationForm generateId={generateId} />
            <Arrow />
            <SearchForLocation createLocation={createLocation} addLocation={addLocation} generateId={generateId}/>
        </div>
    )
};
