import React, {useState, useEffect} from 'react';
import { LocationFrame } from './locationFrame';
import weatherLogo from './resources/WeatherIcons/logo.png';
import { Arrow } from './arrow/arrow';
import '../App.css';
import { database } from './mockBackend/database';
import { AutoComplete } from './autocomplete';

export function Location() {

    const [locations, setLocations] = useState([{
        location: []
    }]);

    const [location, setLocation] = useState([{
        id: '0', //Used as a key to store a location in locations array
        name: 'Placeholder',
        date: '01.02',
        weather: 'sun',
        temp: '18',
        wind: '0'
    }]);


    const createLocation = (dataSource) => {
        setLocation([{
            id: generateId(),
            name: dataSource.name,
            date: dataSource.date,
            weather: dataSource.weather,
            temp: dataSource.temp,
            wind: dataSource.wind
        }])
    }

    const addLocation = (location) => {
        setLocations((prev) => {
            return [...prev, location];
        })
    }


    const removeLocation = (locationID) => {
        setLocations((locations) => {
            locations.filter((location) => location.id !== locationID)
        })
    }


    //Ikke en elegant løsning men enkel
    let idCounter = 0;
    const generateId = () => {
        return idCounter = idCounter + 1;
    }


    useEffect(() => {
        addLocation(createLocation(database));
    }, []);


    return (
        <div className='App'>
            <div className='App-header'>
                <img src={weatherLogo} alt="logo" className='App-logo' />
                <br/>
                <h1 className='App-title'>Forecaster</h1>
                <br/>
            </div>
            <Arrow />
            <AutoComplete addLocation={addLocation} locations={database.name}/>
            {
                locations.map((location) => {
                    return <LocationFrame key={location} id={location.id} name={location.name} weather={location.weather} temp={location.temp} wind={location.wind}/>
                })
            }
        </div>
    )
};
